import React, { useState } from 'react';
import { FaShoppingBasket } from 'react-icons/fa';
import Order from './Order';
import CheckoutModal from './CheckoutModal';

const showOrders = (props) => {
  let summa = 0;
  props.orders.forEach(el => summa += parseInt(el.price.replace(' p', '')));
  
  return (
    <div>
      {props.orders.map(el => (
        <Order 
          key={el.id} 
          item={el} 
          onDelete={props.onDelete} 
        />
      ))}
      <p className='summa'>Сумма: {new Intl.NumberFormat('ru-RU').format(summa)}Р</p>
      <button 
        className='order-button' 
        onClick={props.openCheckout}
        disabled={props.orders.length === 0}
      >
        Оформить заказ
      </button>
    </div>
  );
};

const showNothing = () => {
  return (
    <div className='empty'>
      <h2>Товаров нет</h2>
    </div>
  );
};

export default function Header(props) {
  const [basketOpen, setBasketOpen] = useState(false);
  const [checkoutOpen, setCheckoutOpen] = useState(false);

  const openCheckout = () => {
    setBasketOpen(false);
    setCheckoutOpen(true);
  };

  return (
    <header className="header">
    <div className="header-container">
      <h1 className="logo">House</h1>
      <nav className="main-nav">
        <ul className="nav-list">
          <li className="nav-item"><a href="#">Про нас</a></li>
          <li className="nav-item"><a href="#">Контакты</a></li>
          <li className="nav-item"><a href="#">Кабинет</a></li>
          <li className="nav-item">
            <button className="cart-btn">
              Корзина <span className="cart-count">0</span>
            </button>
          </li>
        </ul>
      </nav>
    </div>
  </header>
  );
}
