import React, { useState, useRef, useEffect } from 'react';
import { FaTimes, FaUser, FaPhone, FaEnvelope, FaArrowRight, FaMapMarkerAlt, FaCreditCard } from 'react-icons/fa';

const CheckoutModal = ({ onClose, orders }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    address: '',
    comment: '',
    cardNumber: '',
    expiry: '',
    cvv: ''
  });

  // Состояния для работы с Google Maps
  const [isMapModalOpen, setIsMapModalOpen] = useState(false);
  const [selectedAddress, setSelectedAddress] = useState('');
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const geocoderRef = useRef(null);

  // Загрузка Google Maps API
  useEffect(() => {
    if (isMapModalOpen && !window.google) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=ВАШ_API_КЛЮЧ&libraries=places&language=ru`;
      script.async = true;
      script.defer = true;
      script.onload = initGoogleMap;
      document.body.appendChild(script);
    } else if (isMapModalOpen && window.google) {
      initGoogleMap();
    }
  }, [isMapModalOpen]);

  const initGoogleMap = () => {
    const map = new window.google.maps.Map(document.getElementById('map'), {
      center: { lat: 55.7558, lng: 37.6173 }, // Москва по умолчанию
      zoom: 12,
      mapTypeControl: false,
      streetViewControl: false
    });

    geocoderRef.current = new window.google.maps.Geocoder();
    mapRef.current = map;

    // Обработчик клика по карте
    map.addListener('click', (e) => {
      geocodePosition(e.latLng);
    });

    // Автозаполнение адреса
    const autocomplete = new window.google.maps.places.Autocomplete(
      document.getElementById('address-input'),
      { types: ['geocode'] }
    );
    autocomplete.bindTo('bounds', map);
    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (!place.geometry) return;
      
      if (place.geometry.viewport) {
        map.fitBounds(place.geometry.viewport);
      } else {
        map.setCenter(place.geometry.location);
        map.setZoom(17);
      }
      
      setSelectedAddress(place.formatted_address);
      placeMarker(place.geometry.location);
    });
  };

  const geocodePosition = (pos) => {
    geocoderRef.current.geocode({ location: pos }, (results, status) => {
      if (status === 'OK' && results[0]) {
        setSelectedAddress(results[0].formatted_address);
        placeMarker(pos);
      }
    });
  };

  const placeMarker = (position) => {
    if (markerRef.current) {
      markerRef.current.setPosition(position);
    } else {
      markerRef.current = new window.google.maps.Marker({
        position,
        map: mapRef.current,
        animation: window.google.maps.Animation.DROP
      });
    }
    mapRef.current.panTo(position);
  };

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const openMapModal = () => setIsMapModalOpen(true);
  const closeMapModal = () => setIsMapModalOpen(false);

  const handleAddressSelect = () => {
    if (selectedAddress) {
      setFormData(prev => ({ ...prev, address: selectedAddress }));
    }
    closeMapModal();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const total = orders.reduce((sum, item) => sum + parseInt(item.price.replace(' p', '')), 0);
    const order = {
      ...formData,
      items: orders,
      total,
      date: new Date().toISOString()
    };
    console.log('Order submitted:', order);
    alert(`Заказ оформлен! Сумма: ${total}Р`);
    onClose();
  };

  const formatPrice = (price) => {
    return parseInt(price.replace(' p', '')).toLocaleString('ru-RU') + 'Р';
  };

  const calculateTotal = () => {
    return orders.reduce((sum, item) => sum + parseInt(item.price.replace(' p', '')), 0);
  };

  return (
    <div className="checkout-modal-overlay">
      <div className="checkout-modal-content">
        <button className="modal-close-btn" onClick={onClose}>
          <FaTimes />
        </button>

        <div className="checkout-progress">
          {[1, 2, 3].map((stepNum) => (
            <div key={stepNum} className={`step ${step >= stepNum ? 'active' : ''}`}>
              <div className="step-number">{stepNum}</div>
              <div className="step-title">
                {stepNum === 1 ? 'Данные' : stepNum === 2 ? 'Оплата' : 'Доставка'}
              </div>
            </div>
          ))}
        </div>

        {step === 1 && (
          <form className="checkout-form" onSubmit={nextStep}>
            <h3>Контактная информация</h3>
            
            <div className="form-group">
              <FaUser className="input-icon" />
              <input
                type="text"
                name="name"
                placeholder="Ваше имя"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <FaPhone className="input-icon" />
              <input
                type="tel"
                name="phone"
                placeholder="Телефон"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <FaEnvelope className="input-icon" />
              <input
                type="email"
                name="email"
                placeholder="Email (необязательно)"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
            
            <button type="submit" className="next-btn">
              Далее <FaArrowRight />
            </button>
          </form>
        )}

        {step === 2 && (
          <form className="payment-form" onSubmit={nextStep}>
            <h3>Данные карты</h3>
            
            <div className="card-preview">
              <div className="card-number">
                {formData.cardNumber ? formData.cardNumber.match(/.{1,4}/g)?.join(' ') || formData.cardNumber : '•••• •••• •••• ••••'}
              </div>
              <div className="card-details">
                <div className="card-name">
                  {formData.name || 'ИМЯ НА КАРТЕ'}
                </div>
                <div className="card-expiry">
                  {formData.expiry || '••/••'}
                </div>
              </div>
            </div>
            
            <div className="form-group">
              <FaCreditCard className="input-icon" />
              <input
                type="text"
                name="cardNumber"
                placeholder="Номер карты"
                value={formData.cardNumber}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '').slice(0, 16);
                  handleChange({ target: { name: 'cardNumber', value } });
                }}
                required
              />
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <input
                  type="text"
                  name="expiry"
                  placeholder="ММ/ГГ"
                  value={formData.expiry}
                  onChange={(e) => {
                    let value = e.target.value.replace(/\D/g, '');
                    if (value.length > 2) {
                      value = value.slice(0, 2) + '/' + value.slice(2, 4);
                    }
                    handleChange({ target: { name: 'expiry', value } });
                  }}
                  maxLength="5"
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  name="cvv"
                  placeholder="CVV"
                  value={formData.cvv}
                  onChange={(e) => {
                    const value = e.target.value.replace(/\D/g, '').slice(0, 3);
                    handleChange({ target: { name: 'cvv', value } });
                  }}
                  maxLength="3"
                  required
                />
              </div>
            </div>
            
            <div className="form-actions">
              <button type="button" className="back-btn" onClick={prevStep}>
                Назад
              </button>
              <button type="submit" className="next-btn">
                Далее <FaArrowRight />
              </button>
            </div>
          </form>
        )}

        {step === 3 && (
          <form className="delivery-form" onSubmit={handleSubmit}>
            <h3>Адрес доставки</h3>
            
            <div className="form-group">
              <FaMapMarkerAlt className="input-icon" />
              <input
                type="text"
                id="address-input"
                name="address"
                placeholder="Адрес доставки"
                value={formData.address}
                onChange={handleChange}
                required
              />
              <button 
                type="button" 
                className="map-btn"
                onClick={openMapModal}
              >
                Выбрать на карте
              </button>
            </div>
            
            <div className="form-group">
              <textarea
                name="comment"
                placeholder="Комментарий к заказу"
                value={formData.comment}
                onChange={handleChange}
                rows="3"
              />
            </div>
            
            <div className="order-summary">
              <h4>Ваш заказ:</h4>
              {orders.map(item => (
                <div key={item.id} className="order-item">
                  <span>{item.title}</span>
                  <span>{formatPrice(item.price)}</span>
                </div>
              ))}
              <div className="order-total">
                <span>Итого:</span>
                <span>{formatPrice(calculateTotal() + ' p')}</span>
              </div>
            </div>
            
            <div className="form-actions">
              <button type="button" className="back-btn" onClick={prevStep}>
                Назад
              </button>
              <button type="submit" className="submit-btn">
                Подтвердить заказ
              </button>
            </div>
          </form>
        )}

        {/* Модальное окно с Google Map */}
        {isMapModalOpen && (
          <div className="map-modal">
            <div className="map-modal-content">
              <button className="close-btn" onClick={closeMapModal}>
                <FaTimes />
              </button>
              <h3>Выберите адрес на карте</h3>
              <div id="map" style={{ width: '100%', height: '400px' }}></div>
              <div className="selected-address">
                <strong>Выбранный адрес:</strong> {selectedAddress || 'Кликните на карте или введите адрес'}
              </div>
              <div className="map-modal-actions">
                <button 
                  type="button" 
                  className="confirm-btn"
                  onClick={handleAddressSelect}
                  disabled={!selectedAddress}
                >
                  Использовать этот адрес
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CheckoutModal;
