import React, { useState } from 'react';
import CustomerStep from './CustomerStep';
import PaymentStep from './PaymentStep';
import DeliveryStep from './DeliveryStep';

const CheckoutModal = ({ orders, onClose }) => {
    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
        customer: {},
        payment: {},
        delivery: {}
    });

    const nextStep = () => setStep(step + 1);
    const prevStep = () => setStep(step - 1);

    const updateFormData = (field, data) => {
        setFormData(prev => ({
            ...prev,
            [field]: { ...prev[field], ...data }
        }));
    };

    const handleSubmit = () => {
        const order = {
            ...formData,
            items: orders,
            total: orders.reduce((sum, item) => sum + parseInt(item.price.replace(' p', '')), 0),
            date: new Date().toISOString()
        };
        console.log('Order submitted:', order);
        onClose();
        
        // Здесь можно добавить отправку данных на сервер
    };

    return (
        <div className="checkout-modal">
            <div className="modal-content">
                <button className="close-button" onClick={onClose}>×</button>
                
                <div className="checkout-steps">
                    <div className={`step ${step >= 1 ? 'active' : ''}`}>1. Данные</div>
                    <div className={`step ${step >= 2 ? 'active' : ''}`}>2. Оплата</div>
                    <div className={`step ${step >= 3 ? 'active' : ''}`}>3. Доставка</div>
                </div>

                {step === 1 && (
                    <CustomerStep 
                        data={formData.customer}
                        updateData={(data) => updateFormData('customer', data)}
                        nextStep={nextStep}
                    />
                )}

                {step === 2 && (
                    <PaymentStep 
                        data={formData.payment}
                        updateData={(data) => updateFormData('payment', data)}
                        nextStep={nextStep}
                        prevStep={prevStep}
                    />
                )}

                {step === 3 && (
                    <DeliveryStep 
                        data={formData.delivery}
                        updateData={(data) => updateFormData('delivery', data)}
                        prevStep={prevStep}
                        onSubmit={handleSubmit}
                    />
                )}
            </div>
        </div>
    );
};

export default CheckoutModal;
