import React, { useState } from 'react';

const PaymentStep = ({ data, updateData, nextStep, prevStep }) => {
  const [formData, setFormData] = useState(data || {});

  const formatCardNumber = (value) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    const matches = v.match(/\d{4,16}/g);
    const match = matches && matches[0] || '';
    const parts = [];
    
    for (let i = 0, len = match.length; i < len; i += 4) {
      parts.push(match.substring(i, i + 4));
    }
    
    return parts.length ? parts.join(' ') : value;
  };

  const formatExpiry = (value) => {
    const v = value.replace(/[^0-9]/gi, '');
    if (v.length >= 3) {
      return `${v.substring(0, 2)}/${v.substring(2)}`;
    }
    return value;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    let formattedValue = value;
    
    if (name === 'cardNumber') {
      formattedValue = formatCardNumber(value);
    } else if (name === 'expiry') {
      formattedValue = formatExpiry(value);
    }
    
    setFormData(prev => ({ ...prev, [name]: formattedValue }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.cardNumber || !formData.expiry || !formData.cvv) {
      alert('Пожалуйста, заполните все поля карты');
      return;
    }
    updateData(formData);
    nextStep();
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <h2>Оплата картой</h2>
      
      <div className="card-preview">
        <div className="card-number">
          {formData.cardNumber?.replace(/\d(?=\d{4})/g, "•") || '•••• •••• •••• ••••'}
        </div>
        <div className="card-details">
          <div className="card-name">
            {formData.cardName || 'ИМЯ НА КАРТЕ'}
          </div>
          <div className="card-expiry">
            {formData.expiry || '••/••'}
          </div>
        </div>
      </div>
      
      <div className="form-group">
        <label>Номер карты*</label>
        <input
          type="text"
          name="cardNumber"
          value={formData.cardNumber || ''}
          onChange={handleChange}
          placeholder="1234 5678 9012 3456"
          maxLength="19"
          required
        />
      </div>
      
      <div className="form-row">
        <div className="form-group">
          <label>Срок действия*</label>
          <input
            type="text"
            name="expiry"
            value={formData.expiry || ''}
            onChange={handleChange}
            placeholder="MM/YY"
            maxLength="5"
            required
          />
        </div>
        
        <div className="form-group">
          <label>CVV*</label>
          <input
            type="text"
            name="cvv"
            value={formData.cvv || ''}
            onChange={handleChange}
            placeholder="123"
            maxLength="3"
            required
          />
        </div>
      </div>
      
      <div className="form-group">
        <label>Имя на карте*</label>
        <input
          type="text"
          name="cardName"
          value={formData.cardName || ''}
          onChange={handleChange}
          required
        />
      </div>
      
      <div className="form-actions">
        <button type="button" className="btn-back" onClick={prevStep}>Назад</button>
        <button type="submit" className="btn-next">Далее</button>
      </div>
    </form>
  );
};

export default PaymentStep;
