import React, { useState } from 'react'
import { FaShoppingBasket } from "react-icons/fa";
import Order from './Order';
const showOrders = (props) =>{
  let summa =0
  return (<div>
    {props.orders.map(el =>(
      <Order onDelete={props.onDelete} key={el.id} Good={el} />
    ))}
    <p className='summa'>Сумма: {summa}$</p>
  </div>)
}
const showNothing = () =>{
  return(<div className='empty'>
    <h2>Товаров нету</h2>
  </div>)
}
export default function Header(props) {
  let[baskOpen,baskcardOpen]=useState(false)
  return (
    <header>
        <div className='head_1'>
            <span className='logo_text'>House</span>
            <ul className='nav'>
              <FaShoppingBasket onClick={()=>baskcardOpen(baskOpen=!baskOpen)} className={`basket ${baskOpen && 'actives'}`}/>
                {baskOpen && (
                  <div className='bask_card'>
                    {props.orders.length> 0 ?
                      showOrders(props): showNothing()}
                  </div>
                )}
              <li>Про нас</li>
              <li>Контакты</li>
              <li>Кабинет</li>
            </ul>
        </div>
        <div className='logo_photo'></div>
    </header>
  )
}
