import React, { Component } from 'react';
import { FaTrash } from "react-icons/fa";

export class Order extends Component {
  render() {
    // Проверяем, что props.Good существует
    if (!this.props.Good) {
      return (
        <div className='item error'>
          <div className='line'></div>
          <div>
            <h1>Товар не найден</h1>
            <b>Нет данных</b>
          </div>
        </div>
      );
    }

    const { Good, onDelete } = this.props;

    return (
      <div className='item'>
        <div className='line'></div>
        {Good.img && (
          <img 
            src={'./img/' + Good.img} 
            alt={Good.title || 'Изображение товара'}
            onError={(e) => {
              e.target.style.display = 'none'; // Скрываем изображение при ошибке загрузки
            }}
          />
        )}
        <div>
          <h1>{Good.title || 'Без названия'}</h1>
          <b>{Good.price || 'Цена не указана'}</b>
          <FaTrash 
            className='delete_prod' 
            onClick={() => onDelete(Good.id)} 
            title="Удалить товар"
          />
        </div>
      </div>
    );
  }
}

export default Order;
