import React from 'react';

const Order = ({ item, onDelete }) => {
  if (!item) {
    return <div className="order-item error">Ошибка: товар не найден</div>;
  }

  return (
    <div className="order-item">
      {item.img && (
        <img 
          src={item.img} 
          alt={item.title || 'Товар'} 
          width={50} 
          onError={(e) => {
            e.target.style.display = 'none';
          }}
        />
      )}
      <div className="order-info">
        <h4>{item.title || 'Без названия'}</h4>
        <p>{item.price || 'Цена не указана'}</p>
      </div>
      <button 
        className="delete-item" 
        onClick={() => onDelete(item.id)}
      >
        ×
      </button>
    </div>
  );
};

export default Order;
