import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Goods from "./components/Goods";
import Categories from "./components/Categories";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orders: [],
            currentGoods: [],
            goods: [
                {
                    id: 1,
                    title: 'Питца',
                    img: 'pizz.jpg',
                    desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
                    category: 'Pizza',
                    price: '968 p'
                },
                {
                    id: 2,
                    title: 'Хот-дог',
                    img: 'hotdog.jpg',
                    desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
                    category: 'Hotdog',
                    price: '168 p'
                },
                {
                    id: 3,
                    title: 'Буррито',
                    img: 'burrito.jpg',
                    desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
                    category: 'Burrito',
                    price: '968 p'
                },
                {
                  id: 4,
                  title: 'Салат',
                  img: 'salat.jpg',
                  desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
                  category: 'Salat',
                  price: '968 p'
              }
            ]
        };
        this.state.currentGoods = this.state.goods;
        this.addToOrder = this.addToOrder.bind(this);
        this.deleteOrder = this.deleteOrder.bind(this);
        this.chooseCategory = this.chooseCategory.bind(this);
    }

    sortGoods(direction) {
        const sortedGoods = [...this.state.currentGoods].sort((a, b) => {
            // Удаляем " p" из цены и преобразуем в число
            const priceA = parseInt(a.price.replace(' p', ''));
            const priceB = parseInt(b.price.replace(' p', ''));

            if (direction === 'asc') {
                return priceA - priceB;
            } else {
                return priceB - priceA;
            }
        });

        this.setState({ currentGoods: sortedGoods });
    }

    chooseCategory(category) {
        if (category === 'all') {
            this.setState({ currentGoods: this.state.goods });
            return;
        }
        this.setState({
            currentGoods: this.state.goods.filter(el => el.category === category)
        });
    }

    deleteOrder(id) {
        this.setState({ orders: this.state.orders.filter(el => el.id !== id) });
    }

    addToOrder(good) {
        let isInArray = false;
        this.state.orders.forEach(el => {
            if (el.id === good.id) isInArray = true;
        });
        if (!isInArray) {
            this.setState({ orders: [...this.state.orders, good] });
        }
    }

    render() {
        return (
            <div className="wrapper">
                <Header orders={this.state.orders} onDelete={this.deleteOrder} />
                <Categories chooseCategory={this.chooseCategory}  sortGoods={this.sortGoods} />
                <Goods goods={this.state.currentGoods} onAdd={this.addToOrder} />
                <Footer />
            </div>
        );
    }
}

export default App;
